mapper.select:
SELECT COUNT(*)
FROM TB_SURVEY
WHERE DEL_YN = 'N'
AND  APRV_STATE = #{aprvState}
AND  REGISTER_ID = #{registerId}

mapper.insert:
INSERT INTO TB_SURVEY (
	  SURVEY_NO
	, BNGN_DTM
	, END_DTM
	, RESLT_RLS_YN
	, TITLE
	, SURVEY_DESC
	, APRV_STATE
	, REGISTER_ID
	, TARGET_YN
	, TARGET_NO
	, RLS_YN
	, TARGET_RLS_YN
) VALUES (
      #{surveyNo}
	, #{bngnDtm}
	, #{endDtm}
	, #{resltRlsYn}
	, #{title}
	, #{surveyDesc}
	, #{aprvState.name}
	, #{registerId}
	, #{targetYn}
	, #{targetNo}
	, #{rlsYn}
	, #{targetRlsYn}
)

mapper.select:
SELECT COUNT(DISTINCT REGISTER_ID) as cnt FROM TB_SURVEY_ANSWER
WHERE
	SURVEY_NO = #{surveyNo}

mapper.update:
UPDATE TB_SURVEY
UPDATER_ID = #{updaterId} , UPD_DTM = current_timestamp(), DEL_YN = 'Y'
SURVEY_NO = #{surveyNo}

UPDATE TB_SURVEY

	  BNGN_DTM = #{bngnDtm}
	, END_DTM = #{endDtm}
	, RESLT_RLS_YN = #{resltRlsYn}
	, TITLE = #{title}
	, TARGET_YN =  #{targetYn}
	, SURVEY_DESC = #{surveyDesc}
	, APRV_STATE = #{aprvState.name}
	, UPDATER_ID = #{updaterId}
	, UPD_DTM = current_timestamp()
	, RLS_YN = #{rlsYn}
	, TARGET_RLS_YN = #{targetRlsYn}
	, TARGET_NO = #{targetNo}


	SURVEY_NO = #{surveyNo}

UPDATE TB_SURVEY

	  APRV_STATE = #{aprvState.name}

		, CHECK_REGISTER_ID = #{checkRegisterId}

		, CHECK_CONT = #{checkCont}

		, CHECK_DTM = current_timestamp()



	SURVEY_NO = #{surveyNo}

UPDATE TB_SURVEY

	  APRV_STATE = #{aprvState.name}

		, CHECK_REGISTER_ID = #{checkRegisterId}

		, CHECK_CONT = #{checkCont}

		, CHECK_DTM = current_timestamp()



	SURVEY_NO = #{surveyNo}

mapper.insert:
INSERT INTO TB_SURVEY_QUESTION (
