	, #{cont}
	, #{esntlYn}
	, #{orderNo}
	, #{surveyNo}
)

mapper.select:
SELECT count(*)
FROM TB_SURVEY_ANSWER
WHERE SURVEY_NO = #{surveyNo}
AND REGISTER_ID = #{registerId}

mapper.insert:
